INSERT INTO `vpw_options` VALUES("196", "widget_kt_image", "a:7:{i:2;a:6:{s:4:\"link\";s:0:\"\";s:6:\"target\";s:3:\"ss1\";s:4:\"size\";s:0:\"\";s:10:\"attachment\";i:41;s:3:\"alt\";s:11:\"Bình thanh\";s:2:\"bg\";i:0;}i:3;a:4:{s:4:\"link\";s:457:\"Đến với Lavita Garden, bạn sẽ đắm chìm \r\ntrong không gian thơ mộng của một khu vườn \r\ntràn đầy sức sống. Hãy để một chồi non \r\nvừa hé thơm dịu, một nhành hoa nở rộ ngát hương \r\ncủa khu vườn dẫn lối cho tâm hồn bạn.\r\n\r\nTại Lavita Garden, bạn sẽ được bình yên \r\nbên tách trà chiều, cùng trò chuyện và tận hưởng\r\nmột cuộc sống thanh bình đáng mơ ước.\";s:3:\"alt\";s:11:\"Khu vườn\";s:6:\"target\";s:3:\"ss2\";s:10:\"attachment\";i:43;}i:4;a:5:{s:4:\"link\";s:247:\"THÔNG TIN DỰ ÁN:\r\nVị trí: Đường số 3, P.Trường Thọ, Q.Thủ Đức, TP.HCM \r\nDiện tích đất toàn khu: 15.314,5m2\r\nDiện tích khu đất chung cư: 9.581,7m2\r\nSố block: 02\r\nTầng cao: 21\r\nTổng số căn hộ: 648 căn\";s:3:\"alt\";s:0:\"\";s:6:\"target\";s:3:\"ss3\";s:10:\"attachment\";i:43;s:4:\"text\";N;}i:5;a:5:{s:4:\"link\";s:688:\"Tại Lavita Garden, chỉ vài bước chân là cư dân đã đến ngay \r\nga số 10 - Bình Thái của tuyến metro Bến Thành - Suối Tiên. \r\nKhi tuyến metro này được đưa vào sử dụng sẽ kết nối Q.Thủ Đức,\r\nQ.2, Q.9 với trung tâm thành phố và các quận lân cận trong\r\nvài phút di chuyển. Tuyến metro được so sánh như mạch máu \r\ngiao thông của TP.HCM sẽ giúp cư dân Lavita Garden dễ dàng \r\ntiếp cận các tiện ích hiện đại như: siêu thị Metro An Phú,\r\nBig C Cantavil, Parkson Cantavil, trường quốc tế BIS,...\r\nvà nhanh chóng hòa mình vào nhịp sống năng động của thành phố.\r\n\";s:3:\"alt\";s:0:\"\";s:6:\"target\";s:3:\"ss4\";s:10:\"attachment\";i:44;s:4:\"text\";N;}i:6;a:4:{s:4:\"link\";s:0:\"\";s:3:\"alt\";s:0:\"\";s:6:\"target\";s:3:\"ss5\";s:10:\"attachment\";i:0;}i:7;a:4:{s:4:\"link\";s:791:\"Từ Lavita Garden chỉ mất 1 phút đi bộ để đến ga số 10 Bình Thái \r\nvà 10 phút di chuyển bằng metro để đến chợ Bến Thành\r\n\r\nGiá chỉ từ 1,1 tỷ/căn, trả góp không lãi suất trong 2,5 năm, \r\nLavita Garden sẽ là lựa chọn lý tưởng dành cho các gia đình trẻ\r\n\r\nKhu vui chơi trẻ em theo tiêu chuẩn Singapore rộng hơn 1.000m2 \r\ncho trẻ em thỏa sức vui chơi phát triển thể lực và trí tuệ\r\n\r\nTích hợp các tiện ích hàng đầu như hồ bơi, nhà hàng, \r\ncafé, gym, spa,…\r\n\r\nMật độ xây dựng thấp, hạ tầng khu vực phát triển đồng bộ\r\n\r\nHung Thinh Corp tự hào là chủ đầu tư tạo được uy tín \r\ntrong lòng Khách hàng qua các công trình đã hiện hữu\";s:3:\"alt\";s:0:\"\";s:6:\"target\";s:3:\"ss6\";s:10:\"attachment\";i:47;}s:12:\"_multiwidget\";i:1;}", "yes");
